-- Fixed FinTech dataset (MySQL)
-- Creates fintech_db, tables, and populates sample data

DROP DATABASE IF EXISTS fintech_db;
CREATE DATABASE fintech_db;
USE fintech_db;

CREATE TABLE customers (
  customer_id INT AUTO_INCREMENT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  gender ENUM('Male','Female'),
  date_of_birth DATE,
  employment_status ENUM('Employed','Self-Employed','Unemployed'),
  monthly_income DECIMAL(12,2),
  state VARCHAR(50),
  registration_date DATE
);

CREATE TABLE accounts (
  account_id INT AUTO_INCREMENT PRIMARY KEY,
  customer_id INT,
  account_type ENUM('Savings','Checking','Loan'),
  balance DECIMAL(12,2),
  opened_date DATE,
  FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE transactions (
  transaction_id INT AUTO_INCREMENT PRIMARY KEY,
  account_id INT,
  transaction_date DATETIME,
  amount DECIMAL(12,2),
  transaction_type ENUM('deposit','withdrawal','transfer','payment'),
  merchant_category VARCHAR(100),
  FOREIGN KEY (account_id) REFERENCES accounts(account_id)
);

CREATE TABLE loans (
  loan_id INT AUTO_INCREMENT PRIMARY KEY,
  customer_id INT,
  loan_type VARCHAR(50),
  principal_amount DECIMAL(12,2),
  monthly_payment DECIMAL(12,2),
  disbursement_date DATE,
  loan_status ENUM('Active','Completed','Defaulted'),
  FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

